import styled from 'styled-components';

const { createGlobalStyle } = styled;

const GlobalStyle = createGlobalStyle`
  :root {
    --theme-primary: #F5005E;
  }
`;

const StyledButton = styled.button`
  font-size: 17px;
  text-transform: uppercase;
  margin: 5px;
  padding: 8px 14px;
  border-radius: 5px;
  font-weight: 700;
  border: 0;
  letter-spacing: 0.5px;
  cursor: ${props => {if (!props.disabled) return 'pointer';}};
  opacity: ${props => {if (props.disabled) return '0.4';}};
  background-color: var(--theme-primary);
  color: ${props => {
    if (props.value === 'Pause' || props.value === 'Fast Forward') return '#000000';
    else return '#ffffff';
  }};
  &:hover {
    transform: ${props => {if (!props.disabled) return 'translateY(-0.1em)';}};
  }
`;

const Button = ({ value, disabledValue, inputTime, onClick }) => {

    return (
        <StyledButton disabled={inputTime ? disabledValue : true} onClick={e => onClick(value)} value={value}>
            {value}
        </StyledButton>
    );
  };

  export default Button;
