import styled from 'styled-components';

const StyledButton = styled.button`
  font-size: 17px;
  text-transform: uppercase;
  margin: 5px;
  padding: 8px 14px;
  border-radius: 5px;
  font-weight: 700;
  border: 0;
  letter-spacing: 0.5px;
  cursor: ${props => {if (!props.disabled) return 'pointer';}};
  opacity: ${props => {if (props.disabled) return '0.4';}};
  background-color: ${props => {
    if (props.value === 'Start') return '#44AF69';
    else if (props.value === 'Pause') return '#FCAB10';
    else if (props.value === 'Resume') return '#2B9EB3';
    else if (props.value === 'Fast Forward') return '#DBD5B5';
    else return '#F8333C';
  }};
  color: ${props => {
    if (props.value === 'Pause' || props.value === 'Fast Forward') return '#000000';
    else return '#ffffff';
  }};
  &:hover {
    background-color: ${props => {
      if (props.value === 'Start') return '#44AF69';
      else if (props.value === 'Pause') return '#FCAB10';
      else if (props.value === 'Resume') return '#2B9EB3';
      else if (props.value === 'Fast Forward') return '#DBD5B5';
      else return '#F8333C';
    }};
  }
`;

const Button = ({ value, disabledValue, inputTime, onClick }) => {

    return (
        <StyledButton disabled={inputTime ? disabledValue : true} onClick={e => onClick(value)} value={value}>
            {value}
        </StyledButton>
    );
  };

  export default Button;
