import { useEffect, useState } from 'react';

const Stopwatch = () => {
    const [time, setTime] = useState(0);
    // const [state, setState] = useState('paused');

    // useEffect(() => {
    //     setInterval(() => {
    //         setTime(time + 1);
    //     }, 1000);
    // }, [time]);

    return (
        <>
            <div>{time}</div>
            <div onClick={() => setInterval(() => {
            setTime(time + 1);
        }, 1000)}>click me</div>
        </>
    );
};

export default Stopwatch;
