import { useEffect, useState } from 'react';

const Stopwatch = () => {
    const [time, setTime] = useState(0);
    // const [state, setState] = useState('paused');

    const run = () => {
        setTime(time + 1);
    };

    useEffect(() => {
        setInterval(() => {
            setTime(time + 1);
        }, 1000);
    }, [time]);

    return (
        <>
            <div>{time}</div>
            <div onClick={run}>click me</div>
        </>
    );
};

export default Stopwatch;
