import { useEffect, useState } from 'react';

const Stopwatch = () => {
    const [time, setTime] = useState(0);
    const [isRunning, setIsRunning] = useState(false);

    useEffect(() => {

        const i = setInterval(() => {
            setTime(time => time + 1);
        }, 1000);

        return () => clearInterval(i);

    }, [time]);

    return (
        <>
            <div>{time}</div>
            <div onClick={() => {}}>Start</div>
        </>
    );
};

export default Stopwatch;
