import { useEffect, useState, useContext } from 'react';
// import { AppContext } from '../../AppProvider';
import Counter from '../generic/Counter';
import { translateFromSeconds } from '../../utils/helpers';
import { useTimeSetter } from '../../utils/hooks';

const Countdown = ({props}) => {

    // const { setcurrentTimerId, isRunningMain } = useContext(AppContext);
    const { inputHours, inputMinutes, inputSeconds } = props;

    const [isRunning, setIsRunning] = useState(false);
    const [isComplete, setIsComplete] = useState(true);
    const [time, setTime] = useState(0);
    const [inputTime, setInputTime] = useState(0);

    useTimeSetter(setInputTime, inputHours, inputMinutes, inputSeconds);
    useTimeSetter(setTime, inputHours, inputMinutes, inputSeconds);

    // const handleClick = value => {
    //     switch(value) {
    //         case 'Start':
    //             setIsRunning(true);
    //             setIsComplete(false);
    //             break;
    //         case 'Pause':
    //             setIsRunning(false);
    //             break;
    //         case 'Resume':
    //             setIsRunning(true);
    //             break;
    //         case 'Fast Forward':
    //             setTime(0);
    //             setIsComplete(true);
    //             break;
    //         case 'Reset':
    //             setTime(inputTime);
    //             setIsComplete(true);
    //             setIsRunning(false);
    //             break;
    //         default:
    //             break;
    //     }
    // }

    useEffect(() => {

        let i;

        if (isRunning) {
            i = setInterval(() => {
                setTime(time - 1);
            }, 1000);
            if (time === 0) {
                clearInterval(i);
                setIsRunning(false);
            }
        }

        return () => clearInterval(i);

    }, [time, inputTime, isRunning]);

    return (
        <Counter>{translateFromSeconds(time)}</Counter>
    );

};

export default Countdown;
