import { useEffect, useState, useContext, useRef } from 'react';
import { AppContext } from '../../AppProvider';
import Counter from '../generic/Counter';
import { translateFromSeconds, translateToSeconds } from '../../utils/helpers';

// const usePrevious = value => {
//     const previous = useRef(0);

//     useEffect(() => {
//       previous.current = value;
//     }, [value]);

//     return previous.current;
// };

const [time, setTime] = useState(translateToSeconds(inputHours, inputMinutes, inputSeconds));
const [isRunning, setIsRunning] = useState(workoutIsRunning);

const Countdown = ({props}) => {

    const { timers, workoutIsRunning, currentTimerId, setCurrentTimerId, setWorkoutIsComplete } = useContext(AppContext);
    const { id, inputHours, inputMinutes, inputSeconds } = props;

    // const prevTime = usePrevious(time);

    useEffect(() => {

        let i;

        if (isRunning && currentTimerId === id) {
            i = setInterval(() => {
                setTime(time - 1);
            }, 1000);
            if (time === 0) {
                clearInterval(i);
                setIsRunning(false);
                if (id < timers.length) {
                    setCurrentTimerId(currentTimerId + 1);
                }
            }
        }

        return () => {
            clearInterval(i);
            if (id === timers.length) {
                setWorkoutIsComplete(true);
            }
        }

    }, [time, isRunning, currentTimerId, id, setCurrentTimerId, timers, setWorkoutIsComplete]);

    const displayTime = (id < currentTimerId) ? '00:00:00' : translateFromSeconds(time);

    return (

        <Counter>{displayTime}</Counter>
    );

};

export default Countdown;
