import styled from 'styled-components';
import trash from '../../images/trash.png';

const StyledCounter = styled.div`
  text-align: center;
  font-family: 'Roboto Mono', monospace;
  font-size: 30px;
  letter-spacing: -0.75px;
  margin: 10px 0;
  display: inline-block;
  padding-left: 5px;
`;

const Counter = ({ duration, progress, removeClick }) => {

  return (
      <div style={{ display: "flex", alignItems: "center", gap: "20px"}}>
        <img src={trash} style={{ width: "20px", height: "25px"}} onClick={removeClick} />
        <div>Duration: <StyledCounter>{duration}</StyledCounter></div>
        <div>Progress: <StyledCounter>{progress}</StyledCounter></div>
      </div>
  );
};

  export default Counter;
