import { useEffect, useState, useContext } from 'react';
import { AppContext } from '../../AppProvider';
import Counter from '../generic/Counter';
import { translateFromSeconds, translateToSeconds } from '../../utils/helpers';

const Countdown = ({props}) => {

    const { timers, workoutIsRunning, setWorkoutIsRunning, currentTimerId, setCurrentTimerId, setWorkoutIsComplete } = useContext(AppContext);
    const { id, inputHours, inputMinutes, inputSeconds } = props;

    const [time, setTime] = useState(translateToSeconds(inputHours, inputMinutes, inputSeconds));
    const [displayTime, setDisplayTime] = useState(translateFromSeconds(time));
    const [isRunning, setIsRunning] = useState(workoutIsRunning);

    useEffect(() => {

        let i;

        if (isRunning && currentTimerId === id) {
            i = setInterval(() => {
                setTime(time - 1);
            }, 1000);
            if (time === 0) {
                clearInterval(i);
                setIsRunning(false);
                if (id < timers.length) {
                    setCurrentTimerId(currentTimerId + 1);
                }
            }
        }

        return () => {
            clearInterval(i);
            setDisplayTime('00:00:00')
            if (id === timers.length) {
                setWorkoutIsComplete(true);
            }
        }

    }, [time, isRunning, currentTimerId, id, setCurrentTimerId, timers, setWorkoutIsComplete]);

    return (

        <Counter>{displayTime}</Counter>
    );

};

export default Countdown;
