import React, { useContext } from "react";
import styled from 'styled-components';
import { ThemeProvider } from 'styled-components';
import trash from '../../images/trash.png';
import { Button, theme } from '../generic/Button';

const StyledCounter = styled.div`
  text-align: center;
  font-family: 'Roboto Mono', monospace;
  font-size: 30px;
  letter-spacing: -0.75px;
  display: inline-block;
  padding: 0 0 20px;
`;

const StyledProgress = styled.span`
  font-size: 20px;
  color: ${props => {if (props.value === 'Complete') return props.theme.start.color;}};
`;

const Counter = ({ label, duration, progress, removeClick }) => {

  return (
      <div style={{ display: "flex", alignItems: "center", gap: "35px",}}>
        <img src={trash} style={{ width: "17px", height: "20px", cursor: "pointer",}} onClick={removeClick} alt="Delete" />
        <div>{label}:<br></br><StyledCounter>{duration}</StyledCounter></div>
        {progress && <div>Progress:<br></br><StyledCounter><StyledProgress>{progress}</StyledProgress></StyledCounter></div>}
      </div>
  );
};

  export default Counter;
