    const { totalTime } = useContext(AppContext);

    const makeButton = ({value}) => {
        return <Button value={value} disabledValue={totalTime ? false : true} onClick={handleClick} />
    };

        const handleClick = value => {};

    return (
        <div style={{ textAlign: "center", margin: "10px 0 20px", display: "flex",}}>
            {makeButton({
                value: "Start"
            })}
            {makeButton({
                value: "Pause"
            })}
            {makeButton({
                value: "Resume"
            })}
            {makeButton({
                value: "Fast Forward"
            })}
            {makeButton({
                value: "Reset"
            })}
        </div>
    );

};

export default ButtonsView;
