import { useEffect, useState } from 'react';
import Button from '../generic/Button';

const Stopwatch = () => {

    const [time, setTime] = useState(0);
    const [inputTime, setInputTime] = useState(0);
    const [isRunning, setIsRunning] = useState(false);
    const [isPaused, setIsPaused] = useState(false);
    const [isComplete, setIsComplete] = useState(true);

    const handleClick = value => {
        if (value === "Start") {
            setIsRunning(true);
            setIsComplete(false);
        } else if (value === "Pause") {
            setIsRunning(false);
            setIsPaused(true);
        } else if (value === "Resume") {
            setIsRunning(true);
            setIsPaused(false);
        } else if (value === 'Fast Forward') {
            setTime(inputTime);
            setIsComplete(true);
        } else if (value === 'Reset') {
            setTime(0);
            setIsComplete(true);
        }
    }

    useEffect(() => {

        let i;

        if (isRunning) {
            i = setInterval(() => {
                setTime(time + 1);
            }, 1000);
            if (time >= inputTime) {
                clearInterval(i);
                setIsRunning(false);
            }
        }

        return () => clearInterval(i);

    }, [time, inputTime, isRunning]);

    return (
        <>
            <div>{time}</div>
            Count to <input type="number" defaultValue={inputTime} onChange={e => {setInputTime(parseInt(e.target.value));}} /> seconds
            <Button value="Start" disabledValue={!isComplete || (time === inputTime) ? true : false} inputTime={inputTime} onClick={handleClick} />
            <Button value="Pause" disabledValue={!isRunning} inputTime={inputTime} onClick={handleClick} />
            <Button value="Resume" disabledValue={(!isRunning && isPaused) ? false : true} inputTime={inputTime} onClick={handleClick} />
            <Button value="Fast Forward" inputTime={inputTime} onClick={handleClick} />
            <Button value="Reset" inputTime={inputTime} onClick={handleClick} />
        </>
    );
};

export default Stopwatch;
