import { useEffect, useState } from 'react';
import Button from '../generic/Button';

const Stopwatch = () => {

    const [time, setTime] = useState(0);
    const [inputTime, setInputTime] = useState(0);
    const [isRunning, setIsRunning] = useState(false);

    const handleClick = e => {
        // Disable button after it's been clicked once
        e.currentTarget.disabled = true;
    }

    useEffect(() => {

        let i;

        if (isRunning) {
            i = setInterval(() => {
                setTime(time + 1);
            }, 1000);
            if (time >= inputTime) {clearInterval(i);}
        }

        return () => clearInterval(i);

    }, [time, inputTime, isRunning]);

    return (
        <>
            <div>{time}</div>
            Count to <input type="number" defaultValue={inputTime} onChange={e => {setInputTime(parseInt(e.target.value));}} /> seconds
            <Button value="Start" disabledValue={!inputTime} />
            <button disabled={!inputTime} onClick={e => {
                setIsRunning(true);
                handleClick(e);
            }}>
                Start
            </button>
            <button onClick={() => {setIsRunning(false);}}>Pause</button>
            <button onClick={() => {setIsRunning(true);}}>Resume</button>
        </>
    );
};

export default Stopwatch;
