import { useContext } from 'react';
import { AppContext } from '../AppProvider';
import { Button } from '../components/generic/Button';

const ButtonsView = () => {

    const { timers, currentTime, paused, setPaused, reset, fastForward, isComplete, currentRound } = useContext(AppContext);

    const handleClick = value => {

        if (value === 'Start' || value === 'Pause' || value === 'Resume') {
            setPaused(!paused);
        } else if (value === 'Fast Forward') {
            fastForward();
        } else if (value === 'Reset') {
            reset();
        }
    };

    const makeButton = ({value}) => {
        return <Button value={value} disabledValue={(timers.length > 0) ? false : true} onClick={handleClick} />
    };

    if (timers.length === 0) {return};

    return (
        <div style={{ margin: "20px 0", display: "flex", justifyContent: "space-between",}}>
            {!isComplete && makeButton({
                value: paused && currentTime === 0 ? 'Start' : paused ? 'Resume' : 'Pause'
            })}
            {makeButton({
                value: "Fast Forward"
            })}
            {/* TODO: The button disappears when the timer increments */}
            {(isComplete || !(currentTime === 0 && currentRound === 1)) && makeButton({
                value: "Reset"
            })}
        </div>
    );

};

export default ButtonsView;
