import { useContext, useState } from 'react';
import { AppContext } from '../../AppProvider';
import { useInterval } from '../../utils/hooks';
import { translateFromSeconds } from '../../utils/helpers';
import Counter from '../generic/Counter';

const Countdown = ({ props }) => {

    const { index, workoutDuration } = props;
    const { activeIndex, paused, setActiveIndex, removeTimer } = useContext(AppContext);
    const [time, setTime] = useState(workoutDuration);
    const active = activeIndex === index;

    useInterval(() => {
        if (paused || !active) return;

        if (time === 0) {
            setActiveIndex(index + 1);
        } else {
            setTime(c => c - 1);
        }
    }, 1000);

    return (
        <>
        <div>Duration<Counter>{translateFromSeconds(workoutDuration)}</Counter></div>
        {(active) && <div>Progress<Counter>{translateFromSeconds(time)}</Counter></div>}
        <button onClick={() => removeTimer(index)}>Remove</button>
        </>
    );

};

export default Countdown;
