    return (
        <div>
            <div style={{ margin: "10px 0 20px", display: "flex",}}>
                {makeButton({
                    value: "Start"
                })}
                {makeButton({
                    value: "Pause"
                })}
                {makeButton({
                    value: "Resume"
                })}
                {makeButton({
                    value: "Fast Forward"
